/**
 * @param {string} str1
 * @param {string} str2
 * @return {string}
 */
var gcdOfStrings = function(str1, str2) {

    if (str1 + str2 !== str2 + str1) return '';
    
    let [longS, shortS] = (str1.length > str2.length) ? [str1, str2] : [str2, str1]
    // if(longS.search(shortS) == -1) return "";

    let findDivisible = (a, b) => (0 === b ? a : findDivisible(b, a % b));

    let gcdLength = findDivisible(str1.length, str2.length)
   
    return shortS.substring(0, gcdLength)
};

